void imprimir_pokemon(const struct pokemon *p)
{
	unsigned int id = p->id;
	switch (id) {
	case 15:
		mostrar_pokemon_1(p);
		break;

	case 7: //charizard
		mostrar_pokemon_2(p);

		break;
	case 25: //pikachu
		mostrar_pokemon_3(p);

		break;
	case 16: //pichu
		mostrar_pokemon_4(p);

		break;
	case 4: //charamdenr
		mostrar_pokemon_5(p);

		break;
	case 27: //Eeve
		mostrar_pokemon_6(p);

		break;
	case 9: //bulbasur
		mostrar_pokemon_7(p);

		break;
	case 46: // lucario
		mostrar_pokemon_8(p);

		break;
	case 145: //togepi
		mostrar_pokemon_9(p);
		break;
	case 26: // ninetails
		mostrar_pokemon_10(p);
		break;
	case 86: //Apharosu
		mostrar_pokemon_11(p);

		break;
	default:
		mostrar_generico(p);
		break;
	}
}

